mapper.select:
 SELECT
    EMP_NO, 
    NAME
FROM EMPLOYEE
WHERE DEPT_CODE = #{dept_code}
AND EMP_NO != #{emp_no}

mapper.update:
 UPDATE RW
SET STATUS = #{status},
	MODIFIED_DATE = SYSDATE

, STATUS_MESSAGE = #{status_message}
, RES_FILE = #{res_file} 

WHERE RW_NO = #{rw_no}

mapper.select:
 SELECT
 	COUNT(*)
 FROM RW A
  JOIN EMPLOYEE B ON A.RES_MEMBER = B.EMP_NO 

A.STATUS = 'CM'


 	AND RES_MEMBER = #{emp_no}


 	AND REQ_MEMBER = #{emp_no}


 	AND (RES_MEMBER = #{emp_no} OR REQ_MEMBER = #{emp_no})







	AND B.NAME LIKE #{keyword}


	AND TITLE LIKE #{keyword}


	AND CONTENT LIKE #{keyword}

SELECT RW_NO, B.NAME AS REQ_MEMBER, C.NAME AS RES_MEMBER, A.IMPORTANT, A.TITLE, TO_CHAR(A.TERM, 'YYYY-MM-DD') AS TERM, TO_CHAR(A.MODIFIED_DATE, 'YYYY-MM-DD') AS MODIFIED_DATE FROM RW A JOIN EMPLOYEE B ON A.REQ_MEMBER = B.EMP_NO JOIN EMPLOYEE C ON A.RES_MEMBER = C.EMP_NO WHERE A.STATUS = 'CM'
AND RES_MEMBER = #{emp_no}
AND REQ_MEMBER = #{emp_no}
AND (RES_MEMBER = #{emp_no} OR REQ_MEMBER = #{emp_no})
AND B.NAME LIKE #{keyword}
AND TITLE LIKE #{keyword}
AND CONTENT LIKE #{keyword}
ORDER BY ${sort}DESC NULLS LAST

 SELECT
    COUNT(*)
FROM RW A
JOIN EMPLOYEE B ON A.RES_MEMBER = B.EMP_NO 



	 WHERE REQ_MEMBER = #{emp_no}


	 WHERE RES_MEMBER = #{emp_no}


	 WHERE (REQ_MEMBER = #{emp_no}
	 OR RES_MEMBER = #{emp_no})




AND IMPORTANT = #{rw.important}


AND A.STATUS = #{rw.status}






	AND C.NAME LIKE #{keyword}


	AND TITLE LIKE #{keyword}


	AND CONTENT LIKE #{keyword}



AND A.STATUS != 'CM'

  SELECT
    RW_NO,
    B.NAME AS REQ_MEMBER,
    C.NAME AS RES_MEMBER,
    A.IMPORTANT,
    A.STATUS,
    A.TITLE,
    TO_CHAR(A.TERM, 'YYYY-MM-DD') AS TERM,
    TO_CHAR(A.CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE
FROM RW A
JOIN EMPLOYEE B ON A.REQ_MEMBER = B.EMP_NO
JOIN EMPLOYEE C ON A.RES_MEMBER = C.EMP_NO
WHERE A.STATUS != 'CM'



	 AND REQ_MEMBER = #{emp_no}


	 AND RES_MEMBER = #{emp_no}


	 AND (REQ_MEMBER = #{emp_no}
	 OR RES_MEMBER = #{emp_no})




AND IMPORTANT = #{rw.important}


AND A.STATUS = #{rw.status}






	AND C.NAME LIKE #{keyword}


	AND TITLE LIKE #{keyword}


	AND CONTENT LIKE #{keyword}




ORDER BY ${sort} DESC NULLS LAST
