mapper.select:
SELECT COUNT(*)
FROM PROJECT_MEMBER
WHERE PJ_NO = #{pj_no}

mapper.update:
UPDATE MB_PJ_TAG
SET TAG_NAME = #{tag_name}
WHERE TAG_NO = #{tag_no}

UPDATE BOARD_REPLY
SET REPLY_CONTENT = #{reply_content}
WHERE REPLY_NO = #{reply_no}

UPDATE PROJECT
SET STATUS = 'N'
WHERE PJ_NO = #{pj_no}

UPDATE PROJECT
SET STATUS = 'Y'
WHERE PJ_NO = #{pj_no}
